import { Plugin } from 'vite';
import { OptimizeOptions } from 'svgo';

interface Options {
    /**
     * [SVGO](https://github.com/svg/svgo) options
     */
    svgoOptions?: OptimizeOptions & {
        /**
         * Enable default SVGO preset
         * Default: true
         */
        presetDefault?: boolean;
    };
    /**
     *  Path folder with svg files
     *  Subfolders are supported
     *  ~or~
     *  Path to already sprite file with symbols
     */
    svgSource?: string;
    /**
     * Prefix for symbols id
     * Default: svg
     */
    symbolPrefix?: string;
    /**
     * Prefix for styles id
     * Default: svg-style
     */
    stylePrefix?: string;
    /**
     * Target for inject sprite in app.html
     * Default: %vite.plugin.sprite%
     */
    injectLabel?: string;
}

declare function sveltekitSprite({ injectLabel, svgSource, symbolPrefix, stylePrefix, svgoOptions, }?: Options): Plugin;

export { sveltekitSprite };
